#!/usr/bin/env bash

# Synopsis:
# Test the test runner by running it against a predefined set of solutions
# with an expected output.

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-tests.sh

exit_code=0

# Iterate over all test directories
for test_dir in tests/*; do
    test_dir_name=$(basename "${test_dir}")
    # hardcoding the slug for now
    slug="MiniBob"
    test_dir_path=$(realpath "${test_dir}")
    results_file="results.json"
    results_file_path="${test_dir}/${results_file}"
    expected_results_file="expected_results.json"
    expected_results_file_path="${test_dir}/${expected_results_file}"
    echo "${test_dir_name}: comparing ${results_file_path} to ${expected_results_file_path}"
    bin/run.sh "${slug}" "${test_dir_path}" "${test_dir_path}"
    if ! diff <(jq --sort-keys . "$results_file_path") <(jq --sort-keys . "$expected_results_file_path"); then
        exit_code=1
    fi
done

exit ${exit_code}
